-- Eliminar usuarios que no tienen datos (del 56 al 60)
DELETE FROM asignacion WHERE id_usuario IN (56, 57, 58, 59, 60);
DELETE FROM usuario WHERE id IN (56, 57, 58, 59, 60);

-- Verificar los datos actualizados
SELECT 
    u.id,
    CONCAT(u.nombre, ' ', u.apellidos) AS nombre_completo,
    u.cargo,
    u.dependencia,
    f.nombre AS formulario_asignado
FROM usuario u
LEFT JOIN asignacion a ON u.id = a.id_usuario
LEFT JOIN formulario f ON a.id_formulario = f.id
ORDER BY u.id;